INSERT INTO `pets_defs` VALUES (778,1663,93352,1571,8,99,'N','',0,187555,'Periwinkle Calf','periwinkle-calf','Periwinkle Calf','U','blue',40,3,18.1159,'','U','Neutral','hippo, Tanaan Jungle','Ground','Pit-pattering footsteps; vocalizations (on-click)','Idles about.','','N','','N','Y','N','N','N','N','N','N',0,'periwinkle-calf.jpg','wow64/icons/inv/babyhippo01.png','wow64/icons/inv/babyhippo01.png','','Right Click to summon and dismiss your Periwinkle Calf.','These playful little riverbeasts enjoy scooping up a large mouthful of water and then spitting it at one another.','|cFFFFD200Drop:|r Fel-Touched Pet Supplies|n|cFFFFD200Zone:|r Tanaan Jungle|n','add	2	Approximate Drop Rate: Unknown\nappend	1	[100]\nlink	Fel-Touched Pet Supplies	http://ptr.wowhead.com/item=127751/fel-touched-pet-supplies','<p><span style=\"color:#FFD200;\">Drop:</span>&nbsp;<a href=\"http://ptr.wowhead.com/item=127751/fel-touched-pet-supplies\" target=\"_blank\">Fel-Touched Pet Supplies</a></p>\n<p><span style=\"color:#FFD200;\">Zone:</span>&nbsp;Tanaan Jungle [100]</p>\n<p><span style=\"color:#FFD200;\">Approximate Drop Rate:</span>&nbsp;Unknown</p>','N','','Fel-Touched Pet Supplies is rewarded upon defeating a legendary \"boss pet\" in Tanaan Jungle.<br><br>\r\nThere are a total of <a href=\"http://www.warcraftpets.com/community/forum/viewtopic.php?f=16&t=14035#p113002\">15 boss pets</a> to battle, each rewarding a single bag. There are no quests to pick up or turn in. Simply defeating a boss pet in Tanaan Jungle will yield a bag.\r\n<br><br>\r\nPlayers can battle each pet only once per day per character, resulting in a maximum of 15 reward bags for each character in one lock-out.<br><br>The Periwinkle Calf is one of four possible pet drops from Fel-Touched Pet Supplies.','Y','Y','N',1372,1,9,'Y',221,2,1,'Y',350,4,1,'N',424,10,9,'Y',436,15,6,'N',376,20,8,'Y',0,0,330,281,297,157,0,'2015-06-23 13:05:47',1,1,1,0,'',5,602000);
