INSERT INTO `pets_defs` VALUES (783,1688,94927,1495,1,67,'N','',0,188235,'Crusher','crusher','Crusher','U','blue',30,3,37.6642,'','U','Neutral','rock golem, pickax, pickaxe, Lunarfall, Frostwall','Ground','Vocalizations (on-click)','Idles about.','','N','','Y','N','N','N','N','N','N','N',0,'crusher.jpg','wow64/icons/achievement/boss/oregorger.png','wow64/icons/achievement/boss/oregorger.png','','Right Click to summon and dismiss Crusher.','Found wandering in a small blizzard, this lost goren carries a mining pick to show that he is a jack of all trades.','|cFFFFD200Vendor: |rTrader Araanda|n|cFFFFD200Zone: |rLunarfall|n|cFFFFD200Cost: |r1000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTrader Darakk|n|cFFFFD200Zone: |rFrostwall|n|cFFFFD200Cost: |r1000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t','link	Trader Araanda	http://ptr.wowhead.com/npc=95470/trader-araanda\nzones	Lunarfall	Lunarfall\nlink	Trader Darakk	http://ptr.wowhead.com/npc=95471/trader-darakk\nzones	Frostwall	Frostwall','<p><span style=\"color:#FFD200;\">Vendor:</span>&nbsp;<a href=\"http://ptr.wowhead.com/npc=95470/trader-araanda\" target=\"_blank\">Trader Araanda</a></p>\n<p><span style=\"color:#FFD200;\">Zone:</span>&nbsp;<a href=\"/wow-pets/map/crusher/lunarfall/\" class=\"zonewithpets\">Lunarfall</a></p>\n<p><span style=\"color:#FFD200;\">Cost:</span>&nbsp;1000<img src=\"/images/wow16/moneyframe/ui-goldicon.png\" style=\"vertical-align:middle;\" alt=\"gold\" title=\"gold\"/></p>\n<p class=\'empty\'>&nbsp;</p>\n<p><span style=\"color:#FFD200;\">Vendor:</span>&nbsp;<a href=\"http://ptr.wowhead.com/npc=95471/trader-darakk\" target=\"_blank\">Trader Darakk</a></p>\n<p><span style=\"color:#FFD200;\">Zone:</span>&nbsp;<a href=\"/wow-pets/map/crusher/frostwall/\" class=\"zonewithpets\">Frostwall</a></p>\n<p><span style=\"color:#FFD200;\">Cost:</span>&nbsp;1000<img src=\"/images/wow16/moneyframe/ui-goldicon.png\" style=\"vertical-align:middle;\" alt=\"gold\" title=\"gold\"/></p>','N','','Crusher was inspired by a young boy\'s idea for a new battle pet. His <a href=\"http://www.warcraftpets.com/news/patch-6-2-ptr-left-shark-crusher-and-achievement/\">mother contacted Blizzard via Twitter</a> and explained that her son was an aspiring developer, going so far as to dress up as a Blizzard developer for \"Future Day\" at school. It was eventually revealed that he had an idea for a new battle pet and even had drawn concept art for it.','Y','Y','N',756,1,9,'Y',1380,2,8,'N',532,4,1,'Y',406,10,1,'Y',1073,15,9,'N',390,20,10,'Y',0,0,366,714,346,411,0,'2015-06-23 13:04:33',1,1,1,0,'',5,602000);
INSERT INTO `pets_defs` VALUES (784,1687,94867,0,9,3,'N','',0,188084,'Left Shark','left-shark','Left Shark','U','blue',20,3,42.1543,'','U','Neutral','garrison, Lunarfall, Frostwall','Ground (hovers)','Vocalizations (on-click)','Idles about and sometimes spins around in place.','','N','','N','Y','N','N','N','N','N','N',0,'left-shark.jpg','wow64/icons/inv/pet/babyshark.png','wow64/icons/inv/pet/babyshark.png','','Right Click to summon and dismiss your Left Shark.','Who would leave such a cute shark behind? The Iron Horde truly has no soul.','|cFFFFD200Garrison Mission:|r Orphaned Aquatic Animal Rescue|n','append	0	\nadd	1	Zone: Lunarfall, Frostwall\nlink	Orphaned Aquatic Animal Rescue	http://ptr.wowhead.com/mission=619/orphaned-aquatic-animal-rescue\nzones	Lunarfall	Lunarfall\nzones	Frostwall	Frostwall','<p><span style=\"color:#FFD200;\">Garrison Mission:</span>&nbsp;<a href=\"http://ptr.wowhead.com/mission=619/orphaned-aquatic-animal-rescue\" target=\"_blank\">Orphaned Aquatic Animal Rescue</a> </p>\n<p><span style=\"color:#FFD200;\">Zone:</span>&nbsp;<a href=\"/wow-pets/map/left-shark/lunarfall/\" class=\"zonewithpets\">Lunarfall</a>, <a href=\"/wow-pets/map/left-shark/frostwall/\" class=\"zonewithpets\">Frostwall</a></p>','N','','<a href=\"http://ptr.wowhead.com/mission=619/orphaned-aquatic-animal-rescue\" target=\"_blank\">Orphaned Aquatic Animal Rescue</a> is a rare naval mission. <br><br>\r\nThis Ship-Combat mission takes 1 day, costs 100 oil, and requires 2 ships. It will remain available in your command table for 21 days, or until completed.\r\n<br><br>\r\nA prerequisite mission must be completed before gaining access to Orphaned Aquatic Animal Rescue. After completing <a href=\"http://ptr.wowhead.com/search?q=blockade%3A+south+sea\" target=\"_blank\">Blockade: South Sea</a>, a larger mission area is unlocked on the map.\r\n<br><br>\r\nThere are five threats to counter: Stormy Weather, Destroyer, Minelayer, Submarine and First Strike. ','Y','Y','N',118,1,9,'Y',419,2,9,'Y',276,4,9,'Y',424,10,9,'Y',366,15,5,'N',1062,20,9,'N',0,0,367,386,316,206,0,'2015-06-23 13:05:19',1,1,1,0,'',5,602000);
INSERT INTO `pets_defs` VALUES (785,1662,93143,1530,8,10,'N','',0,184482,'Cinder Pup','cinder-pup','Cinder Pup','U','blue',20,3,39.5856,'','U','Neutral','fire dog, garrison, Lunarfall, Frostwall','Ground','Vocalizations (on-click)','Sniffs the ground and scratches itself.','','N','','N','Y','N','N','N','N','N','N',0,'cinder-pup.jpg','wow64/icons/inv/pet/frostwolfpup_fire.png','wow64/icons/inv/pet/frostwolfpup_fire.png','','Right Click to summon and dismiss your Cinder Pup.','Found munching on a blackened bone in the Black Forge of Blackrock Foundry, this pup really seems to warm up to you quickly.','|cFFFFD200Garrison Mission:|r Fiery Friends|n','append	0	[100]\nadd	1	Zone: Lunarfall, Frostwall\nlink	Fiery Friends	http://ptr.wowhead.com/mission=678/fiery-friends\nzones	Lunarfall	Lunarfall\nzones	Frostwall	Frostwall','<p><span style=\"color:#FFD200;\">Garrison Mission:</span>&nbsp;<a href=\"http://ptr.wowhead.com/mission=678/fiery-friends\" target=\"_blank\">Fiery Friends</a> [100]</p>\n<p><span style=\"color:#FFD200;\">Zone:</span>&nbsp;<a href=\"/wow-pets/map/cinder-pup/lunarfall/\" class=\"zonewithpets\">Lunarfall</a>, <a href=\"/wow-pets/map/cinder-pup/frostwall/\" class=\"zonewithpets\">Frostwall</a></p>','N','','<a href=\"http://ptr.wowhead.com/mission=678/fiery-friends\" target=\"_blank\">Fiery Friends</a> is a rare, level 100 follower mission.\r\n<br><br>It is a 12 hour long mission, costs 100 garrison resources, and requires three item level 675 followers. It will remain available in your command table for seven days, or until completed.<br><br>There are six threats to counter: Massive Strike, Timed Battle, Powerful Spell, Minion Swarms, Deadly Minions, and Group Damage.','Y','Y','N',1359,1,8,'Y',409,2,7,'Y',176,4,7,'Y',501,10,2,'Y',165,15,5,'N',362,20,8,'N',0,0,425,847,351,523,0,'2015-06-23 13:04:08',1,1,1,0,'',5,602000);
