INSERT INTO `pets_defs` VALUES (822,1933,112945,220,7,43,'N','',0,224786,'Nightmare Treant','nightmare-treant','Nightmare Treant','U','blue',50,3,5.2771,'','U','Neutral','achievement','Ground','Pit-pattering footsteps; vocalizations (on-click)','Idles about.','','N','','N','N','N','N','Y','N','N','N',0,'nightmare-treant.jpg','wow64/icons/spell/nature/thorns_nightmare.png','wow64/icons/spell/nature/thorns_nightmare.png','','Right Click to summon and dismiss this companion.','Didn\'t play nice with the other plants in the nursery.','|cFFFFD200Achievement: |rFamily Familiar|n|cFFFFD200Category: |rBattle','link	Family Familiar	http://legion.wowhead.com/achievement=9696/family-familiar','<p><span style=\"color:#FFD200;\">Achievement:</span>&nbsp;<a href=\"http://legion.wowhead.com/achievement=9696/family-familiar\" target=\"_blank\">Family Familiar</a></p>\n<p><span style=\"color:#FFD200;\">Category:</span>&nbsp;Battle</p>','N','','To collect this pet players must complete the following achievements:<br>\r\n<ul><li><a href=\"http://legion.wowhead.com/achievement=9686\" target=\"_blank\">Aquatic Acquiescence</a></li><li><a href=\"http://legion.wowhead.com/achievement=9687\" target=\"_blank\">Best of the Beasts</a></li><li><a href=\"http://legion.wowhead.com/achievement=9689\" target=\"_blank\">Dragons!</a></li><li><a href=\"http://legion.wowhead.com/achievement=9691\" target=\"_blank\">Flock Together</a></li><li><a href=\"http://legion.wowhead.com/achievement=9693\" target=\"_blank\">Master of Magic</a></li><li><a href=\"http://legion.wowhead.com/achievement=9688\" target=\"_blank\">Mousing Around</a></li><li><a href=\"http://legion.wowhead.com/achievement=9692\" target=\"_blank\">Murlocs, Harpies, and Wolvar, Oh My!</a></li><li><a href=\"http://legion.wowhead.com/achievement=9690\" target=\"_blank\">Ragnaros, Watch and Learn</a></li><li><a href=\"http://legion.wowhead.com/achievement=9694\" target=\"_blank\">Roboteer</a></li><li><a href=\"http://legion.wowhead.com/achievement=9695\" target=\"_blank\">The Lil\' Necromancer</a></li></u><br><br>\r\nThese achievements challenge the player to defeat 12 out 15 Master Tamers on the Broken Isles with teams comprised of pets from the same family type.<br><br>\r\nMaster Tamers will occasionally pop up through the World Quest system for level 110 players. 3 random Pet Battle quests pop up daily, but it\'s entirely possible that a Master Tamer will not appear at all on any given day.<br><br>\r\nProgress on these achievements are tracked account-wide, so players can use alts to battle a specific Master Tamer using different pet-family teams to advance through the achievement faster.','Y','N','Y',394,1,7,'Y',1731,2,6,'N',256,4,1,'Y',398,10,7,'Y',745,15,7,'Y',400,20,7,'Y',0,0,413,170,346,90,0,'2016-08-02 00:00:00',1,1,1,0,'',6,700030);
