INSERT INTO `pets_defs` VALUES (874,1706,96649,205,8,9,'N','',0,191425,'Ashmaw Cub','ashmaw-cub','Ashmaw Cub','U','blue',30,3,27.6034,'','P','Neutral','bear, Val\'sharah','Ground','Vocalizations (on-click)','Idles about.','','N','','N','N','Y','N','N','N','N','N',0,'ashmaw-cub.jpg','wow64/icons/inv/misc/bearcubbrown.png','wow64/icons/inv/misc/bearcubbrown.png','','','This little guy has had a rough start at life, be sure to take good care of him.','|cFFFFD200Quest:|r Adopting the Adorable|n|cFFFFD200Zone:|r Val\'Sharah','append	0	[98]\nlink	Adopting the Adorable	http://www.wowhead.com/quest=38889/adopting-the-adorable\nzones	Val\'Sharah	Val\'Sharah','<p><span style=\"color:#FFD200;\">Quest:</span>&nbsp;<a href=\"http://www.wowhead.com/quest=38889/adopting-the-adorable\" target=\"_blank\">Adopting the Adorable</a> [98]</p>\n<p><span style=\"color:#FFD200;\">Zone:</span>&nbsp;<a href=\"/wow-pets/map/ashmaw-cub/val_sharah/\" class=\"zonewithpets\">Val\'Sharah</a></p>','N','','Players can collect this pet by heading east of Smolderhide Thicket in Val\'sharah.<br><br>\r\nA <a href=\"http://www.wowhead.com/npc=93677/shivering-ashmaw-cub\" target=\"_blank\">Shivering Ashmaw Cub</a> can be found in small cave in Lunarwing Shallows. Interacting with this NPC starts a small event in which Thistleleaf sprites attack, followed by <a href=\"http://www.wowhead.com/npc=93686/jinikki-the-puncturer\" target=\"_blank\">Jinikki the Puncturer</a>. Defeating these hostile mobs will allow the player to speak with the Shivering Ashmaw Cub again, which will reward the pet.<br><br>\r\nPlayers can start and complete this event at any time. There are no prerequisites.','Y','Y','N',429,1,8,'Y',247,2,8,'N',345,4,8,'Y',347,10,8,'Y',348,15,8,'N',124,20,8,'Y',0,0,291,401,294,260,0,'2016-08-02 00:00:00',1,1,1,0,'',6,700030);
