INSERT INTO `pets_defs` VALUES (890,1919,111296,1918,1,121,'N','',0,221683,'Horde Fanatic','horde-fanatic','Horde Fanatic','U','blue',50,3,6.4507,'pvp / ho','P','Horde-only','PVP, prestige','Ground','Unknown','Idles about.','','N','','N','N','N','N','Y','N','N','Y',0,'horde-fanatic.jpg','wow64/icons/inv/pet/wyrmtongue.png','wow64/icons/inv/pet/wyrmtongue.png','','','Puts the fan in fanatic!','|cFFFFD200PvP:|r Prestige Reward','','<p><span style=\"color:#FFD200;\">PvP:</span>&nbsp;Prestige Reward</p>','N','','Upon reaching Honor Level 50 for the first time, Horde characters are rewarded with this companion pet.','Y','N','Y',1719,1,1,'N',1556,2,1,'Y',1722,4,1,'N',111,10,1,'Y',1720,15,1,'N',1557,20,7,'N',0,0,377,189,360,95,0,'2016-08-02 00:00:00',1,1,1,0,'',6,700030);
INSERT INTO `pets_defs` VALUES (891,1920,111425,446,6,69,'N','',0,221906,'Transmutant','transmutant','Transmutant','U','blue',40,3,10.0261,'','P','Neutral','green blob, globule, slime, ooze, alchemy','Ground','None','Bubbles and idles about.','','N','','N','N','N','Y','N','N','N','N',0,'transmutant.jpg','wow64/icons/inv/misc/endlesspotion.png','wow64/icons/inv/misc/endlesspotion.png','','Right Click to summon and dismiss this companion.','When alchemy goes wrong... So, so, wrong.','|cFFFFD200Profession:|r Alchemy|n','append	0	[700]\nadd	1	\nadd	2	Drop: Pulsating Sac\nadd	3	Approximate Drop Rate: Unknown\nlink	Pulsating Sac	http://legion.wowhead.com/item=137599/pulsating-sac','<p><span style=\"color:#FFD200;\">Profession:</span>&nbsp;Alchemy [700]</p>\n<p class=\'empty\'>&nbsp;</p>\n<p><span style=\"color:#FFD200;\">Drop:</span>&nbsp;<a href=\"http://legion.wowhead.com/item=137599/pulsating-sac\" target=\"_blank\">Pulsating Sac</a></p>\n<p><span style=\"color:#FFD200;\">Approximate Drop Rate:</span>&nbsp;Unknown</p>','N','','Alchemists will need to complete a series of quests given by the trainer in Dalaran (Broken Isles) to fix the Dalaran Alchemy Station.<br><br>\r\nOnce players have advanced the quest chain far enough to gain access to Leyflame Burner, Advanced Corks, Dual-Chambered Mixing Flask, and Precipitating Powder, players will be able to purchase <a href=\"http://legion.wowhead.com/item=128209\" target=\"_blank\">Recipe: Wild Transmutation</a> from <a href=\"http://legion.wowhead.com/npc=92457/patricia-egan\" target=\"_blank\">Patricia Egan</a>. The quest to obtain the powder becomes available at level 106.<br><br>\r\nPerforming a Wild Transmutation will teach the alchemist a random new transmutation recipe.<br><br>\r\n<a href=\"http://legion.wowhead.com/spell=213256/transmute-meat-to-pet\" target=\"_blank\">Transmute: Meat to Pet</a> (requires Alchemy skill 700) is the recipe that creates Pulsating Sac, which has a chance to drop a number of different pets, 3 of which are new - <a href=\"/?pet=ridgeback piglet\">Ridgeback Piglet</a>, <a href=\"/?pet=thaumaturgical piglet\">Thaumaturgical Piglet</a>, and \r\nTransmutant.','Y','Y','N',1724,1,7,'N',447,2,6,'Y',450,4,6,'Y',449,10,4,'Y',486,15,6,'Y',277,20,6,'Y',0,0,354,143,329,80,0,'2016-08-02 00:00:00',1,1,1,0,'',6,700030);
INSERT INTO `pets_defs` VALUES (892,1921,111423,136,6,70,'N','',0,221907,'Untethered Wyrmling','untethered-wyrmling','Untethered Wyrmling','U','blue',30,3,26.1918,'rep','P','Neutral','Suramar','Air (flies alongside flying mount)','Vocalizations (on-click)','Hovers about.','','N','','N','N','Y','N','N','N','N','N',0,'untethered-wyrmling.jpg','wow64/icons/inv/elemental/mote_mana.png','wow64/icons/inv/elemental/mote_mana.png','','Right Click to summon and dismiss this companion.','A wyrmlings temperment hinges primarily on how long its been since its fed on mana. Remember to feed your pets.','|cFFFFD200Quest: |rBalance to Spare|n|cFFFFD200Zone: |rSuramar','append	0	[110]\nlink	Balance to Spare	http://www.wowhead.com/quest=42837/balance-to-spare\nzones	Suramar	Suramar','<p><span style=\"color:#FFD200;\">Quest:</span>&nbsp;<a href=\"http://www.wowhead.com/quest=42837/balance-to-spare\" target=\"_blank\">Balance to Spare</a> [110]</p>\n<p><span style=\"color:#FFD200;\">Zone:</span>&nbsp;<a href=\"/wow-pets/map/untethered-wyrmling/suramar/\" class=\"zonewithpets\">Suramar</a></p>','N','','The quest that rewards this pet can be found at the Twilight Vineyard quest hub in Suramar.<br><br>\r\nPlayers must have a reputation of Honored or higher with the Nightfallen to gain access to this questing area.','Y','Y','N',484,1,6,'Y',486,2,6,'Y',489,4,6,'Y',360,10,5,'Y',488,15,6,'N',490,20,1,'N',0,0,333,142,323,85,0,'2016-08-02 00:00:00',1,1,1,0,'',6,700030);
INSERT INTO `pets_defs` VALUES (893,1922,111421,0,8,21,'N','',0,221908,'Lurking Owl Kitten','lurking-owl-kitten','Lurking Owl Kitten','U','blue',30,3,27.4653,'','P','Neutral','owl-cat, Suramar','Ground','Vocalizations (on-click)','Idles about.','','N','','N','N','Y','N','N','N','N','N',0,'lurking-owl-kitten.jpg','wow64/icons/ability/druid/catformattack.png','wow64/icons/ability/druid/catformattack.png','','Right Click to summon and dismiss this companion.','It\'s a bird! It\'s a cat! It\'s... biting me...','|cFFFFD200Quest: |rYou\'ve Got to Be Kitten Me Right Meow|n|cFFFFD200Zone: |rSuramar','append	0	[110]\nzones	Suramar	Suramar\nlink	You\'ve Got to Be Kitten Me Right Meow	http://www.wowhead.com/quest=41197/youve-got-to-be-kitten-me-right-meow','<p><span style=\"color:#FFD200;\">Quest:</span>&nbsp;<a href=\"http://www.wowhead.com/quest=41197/youve-got-to-be-kitten-me-right-meow\" target=\"_blank\">You\'ve Got to Be Kitten Me Right Meow</a> [110]</p>\n<p><span style=\"color:#FFD200;\">Zone:</span>&nbsp;<a href=\"/wow-pets/map/lurking-owl-kitten/suramar/\" class=\"zonewithpets\">Suramar</a></p>','N','','Players must be level 110 to gain access to Suramar.<br><br>To access the quest that rewards this pet, players must first complete a quest chain that starts in Dalaran.<br><br> Khadgar will have a quest for level 110 players called <a href=\"http://www.wowhead.com/quest=39985/khadgars-discovery\" target=\"_blank\">Khadgar\'s Discovery</a>. Completing this quest chain will satisfy the \'Nightfall\' objective in the meta-achievement <a href=\"http://www.wowhead.com/achievement=10617/nightfallen-but-not-forgotten\" target=\"_blank\">Nightfallen But Not Forgotten</a>.<br><br>After doing this, a set of quests will unlock at a small camp northeast of Falanaar, the last being the quest that rewards the Lurking owl Kitten.<br><br> Read more about this pet in our <a href=\"http://www.warcraftpets.com/news/lurking-owl-kitten-pet-of-the-month-september-2016/\">Pet of the Month</a>!','Y','Y','N',429,1,8,'Y',492,2,8,'Y',1398,4,3,'Y',184,10,3,'Y',357,15,8,'Y',536,20,8,'N',0,0,404,204,343,107,0,'2016-08-02 00:00:00',1,1,1,0,'',6,700030);
INSERT INTO `pets_defs` VALUES (894,1927,17254,343,8,11,'N','',0,30152,'Ash\'ana','ash-ana','Ash\'ana','U','purple',30,3,22.831,'rep','P','Neutral','cat, white tiger, cub, kitten, Val\'sharah','Ground','Pit-pattering footsteps; vocalizations (periodic and on-click)','Yawns, sits, and lies down.','Emoting /sit while targeting it will cause it to walk over, jump onto your lap and lie down.','N','','Y','N','N','N','N','N','N','N',0,'ash-ana.jpg','wow64/icons/inv/misc/head_tiger_01.png','wow64/icons/ability/mount/whitetiger.png','','Right Click to summon and dismiss your tiger cub.','One of Ash\'alah\'s litter, entrusted to you by the Dreamweavers.','|cFFFFD200Vendor: |rSylvia Hartshorn|n|cFFFFD200Zone: |rVal\'sharah|n|cFFFFD200Faction: |rDreamweavers - Revered|n|cFFFFD200Cost: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t','append	3	(unlimited supply)\nlink	Sylvia Hartshorn	http://legion.wowhead.com/npc=106901/sylvia-hartshorn\nzones	Val\'sharah	Val\'sharah','<p><span style=\"color:#FFD200;\">Vendor:</span>&nbsp;<a href=\"http://legion.wowhead.com/npc=106901/sylvia-hartshorn\" target=\"_blank\">Sylvia Hartshorn</a></p>\n<p><span style=\"color:#FFD200;\">Zone:</span>&nbsp;<a href=\"/wow-pets/map/ash-ana/val_sharah/\" class=\"zonewithpets\">Val\'sharah</a></p>\n<p><span style=\"color:#FFD200;\">Faction:</span>&nbsp;Dreamweavers - Revered</p>\n<p><span style=\"color:#FFD200;\">Cost:</span>&nbsp;500<img src=\"/images/wow16/moneyframe/ui-goldicon.png\" style=\"vertical-align:middle;\" alt=\"gold\" title=\"gold\"/> (unlimited supply)</p>','N','','Players will need a reputation of Revered or higher with <a href=\"http://legion.wowhead.com/faction=1883/dreamweavers\" target=\"_blank\">Dreamweavers</a> before being able to purchase this pet.<br><br>\r\n\r\nCompleting quests in Val\'sharah will reward Dreamweavers reputation. Max level players can complete World Quests and complete Dreamweaver Emissary objectives to earn large chunks of reputation.','Y','Y','Y',429,1,8,'Y',4